import React, { useContext } from "react";
import { AppContext } from '../../context/AppContext';
import { RiDeleteBin6Line, RiCheckboxBlankCircleLine } from "react-icons/ri";

function ExpenseItem(props) {
    const { dispatch } = useContext(AppContext);

    function handleDeleteExpense() {
        dispatch({
            type: 'DELETE-EXPENSE',
            payload: props.id,
        });
    };

    return (
        <li key={props.id} className='list-group-item d-flex justify-content-between align-items-center'>
            <p className="mt-3">
                <span> <svg stroke="#2A8C46" fill="#2A8C46" stroke-width="0" viewBox="0 0 24 24" height="1em" width="1em" xmlns="http://www.w3.org/2000/svg"><g><path fill="#2A8C46" d="M0 0h24v24H0z"></path><path d="M12 22C6.477 22 2 17.523 2 12S6.477 2 12 2s10 4.477 10 10-4.477 10-10 10zm0-2a8 8 0 1 0 0-16 8 8 0 0 0 0 16z"></path></g></svg> </span>
                {props.name}
            </p>
            <div>
                <span>
                    ${props.cost}
                </span>
                <div>
                    EDIT
                </div>
                <RiDeleteBin6Line onClick={handleDeleteExpense}></RiDeleteBin6Line>
            </div>

        </li>
    );
}

export default ExpenseItem;
