import React, { useContext } from "react";
import { AppContext } from '../../context/AppContext';
import { RiDeleteBin6Line, RiCheckboxBlankCircleLine } from "react-icons/ri";

function ExpenseItem(props) {
    const { dispatch } = useContext(AppContext);

    const handleEditClick = () => {
        setIsEditing(true);
    };

    const handleSaveClick = (value) => {
        dispatch({
            type: 'SET_EXPENSE',
            payload: value,
        });
        setIsEditing(false);
    };

    function handleDeleteExpense() {
        dispatch({
            type: 'DELETE-EXPENSE',
            payload: props.id,
        });
    };

    return (
        <li className='list-group-item d-flex justify-content-between align-items-center'>
            <p className="mt-3">
                <RiCheckboxBlankCircleLine></RiCheckboxBlankCircleLine>
                {props.name}
            </p>
            <div>

                <div class='alert alert-secondary p-3 d-flex align-items-center justify-content-between'>

                    {isEditing ? (
                        <EditExpense handleSaveClick={handleSaveClick} expenses={expenses} />
                    ) : (
                        <span className="badge-primary badge-pill mr-3">
                            ${props.cost}
                            <button type='button' className='btn' onClick={handleEditClick}>
                                Edit
                            </button>
                        </span>
                    )}
                </div>
                <RiDeleteBin6Line onClick={handleDeleteExpense}></RiDeleteBin6Line>
            </div>

        </li>
    );
}

export default ExpenseItem;
